def detect_marker(data, window_sz):
    for i in range(0, len(data)-window_sz):
        if len(set(data[i:(i+window_sz)])) == window_sz:
            return i+window_sz


def main():
    datastream = ""
    packet_marker = 4
    message_marker = 14
    print("The first packet marker is after character " +
          str(detect_marker(datastream, packet_marker)))
    print("The first message marker is after character " +
          str(detect_marker(datastream, message_marker)))


if __name__ == "__main__":
    main()
